pragma solidity ^0.8.20;
pragma abicoder v2;

import "../utils/EnumerableValues.sol";
import {Position, PositionProps} from "./../types/PositionStruct.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import {PositionStorage, PositionCache} from "./PositionStorage.sol";

library PositionHandler {
    using Position for PositionProps;
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableValues for EnumerableSet.AddressSet;
    using SafeCast for uint256;
    using SafeCast for int256;
    using SafeCast for int128;
    using SafeCast for uint128;

    function increasePosition(
        PositionCache memory cache
    ) internal returns (PositionProps memory result) {
        cache.isOpen = true;
        cache.sk = PositionStorage.storageKey(cache.market, cache.isLong);
        cache.position = PositionStorage.Storage().positions[cache.sk][
            cache.account
        ];

        if (cache.position.size == 0)
            cache.position.averagePrice = uint128(cache.markPrice);

        if (cache.position.size > 0 && cache.sizeDelta > 0) {
            (bool _hasProfit, uint256 _realisedPnl) = cache.position.calPNL(
                cache.markPrice
            );
            cache.position.averagePrice = cache.position.calAveragePrice(
                cache.sizeDelta,
                cache.markPrice,
                _realisedPnl,
                _hasProfit
            );

            int256 _pnl = _hasProfit
                ? int256(_realisedPnl)
                : -int256(_realisedPnl);

            result.realisedPnl = _pnl;
            result.averagePrice = cache.position.averagePrice;
        }

        cache.position.collateral = (cache.position.collateral.toInt256() +
            cache.collateralDelta).toUint256();
        cache.position.entryFundingRate = cache.fundingRate;
        cache.position.size = cache.position.size + cache.sizeDelta;
        cache.position.isLong = cache.isLong;
        cache.position.lastTime = uint32(block.timestamp);

        require(cache.position.isValid(), "positionBook: invalid position");
        cache.globalPosition = _calGlobalPosition(cache);
        PositionStorage.set(cache);
        result.size = cache.position.size;
        result.collateral = cache.position.collateral;
    }

    function decreasePosition(
        PositionCache memory cache
    ) internal returns (PositionProps memory result) {
        cache.isOpen = false;
        cache.sk = PositionStorage.storageKey(cache.market, cache.isLong);
        cache.position = PositionStorage.Storage().positions[cache.sk][
            cache.account
        ];
        require(
            cache.position.lastTime != uint32(block.timestamp),
            "pb:same block"
        );
        require(cache.position.isValid(), "positionBook: invalid position");
        if (cache.collateralDelta > 0) {
            require(
                cache.position.collateral >= cache.collateralDelta.toUint256(),
                "positionBook: invalid collateral"
            );
        }
        require(
            cache.position.size >= cache.sizeDelta,
            "positionBook: invalid size"
        );
        if (cache.position.size != cache.sizeDelta) {
            cache.position.entryFundingRate = cache.fundingRate;
            cache.position.size = cache.position.size - cache.sizeDelta;
            cache.position.collateral = (cache.position.collateral.toInt256() -
                cache.collateralDelta).toUint256();
            require(cache.position.isValid(), "positionBook: invalid position");
            cache.globalPosition = _calGlobalPosition(cache);
            PositionStorage.set(cache);
            result.size = cache.position.size;
            result.collateral = cache.position.collateral;
        } else {
            PositionStorage.remove(cache);
        }
    }

    function liquidatePosition(
        PositionCache memory cache
    ) internal returns (PositionProps memory result) {
        cache.isOpen = false;
        cache.sk = PositionStorage.storageKey(cache.market, cache.isLong);
        cache.position = PositionStorage.Storage().positions[cache.sk][
            cache.account
        ];
        require(
            cache.position.isExist(),
            "positionBook: position does not exist"
        );
        if (cache.markPrice != 0) {
            (bool _hasProfit, uint256 _realisedPnl) = cache.position.calPNL(
                cache.markPrice
            );
            int256 _pnl = _hasProfit
                ? int256(_realisedPnl)
                : -int256(_realisedPnl);

            result.realisedPnl = _pnl;
        }
        PositionStorage.remove(cache);
        result.size = cache.position.size;
        result.collateral = cache.position.collateral;
    }

    // =====================================================
    //           internal only
    // =====================================================

    function _calGlobalPosition(
        PositionCache memory cache
    ) internal view returns (PositionProps memory) {
        PositionProps memory _position = PositionStorage
            .Storage()
            .globalPositions[cache.sk];
        if (cache.isOpen) {
            uint256 _averagePrice = _calGlobalAveragePrice(
                _position,
                cache.sizeDelta,
                cache.markPrice
            );
            require(_averagePrice > 100, "pb:invalid global position");
            _position.averagePrice = _averagePrice;
            _position.size += cache.sizeDelta;
            _position.collateral = (_position.collateral.toInt256() +
                cache.collateralDelta).toUint256();
            _position.isLong = cache.isLong;
            _position.lastTime = uint32(block.timestamp);

            return _position;
        }

        _position.size -= cache.sizeDelta;
        _position.collateral -= cache.collateralDelta.toUint256();

        return _position;
    }

    function _calGlobalAveragePrice(
        PositionProps memory position,
        uint256 sizeDelta,
        uint256 markPrice
    ) internal pure returns (uint256) {
        if (position.size == 0) {
            return markPrice;
        }
        if (position.size > 0 && sizeDelta > 0) {
            (bool _hasProfit, uint256 _pnl) = position.calPNL(markPrice);
            position.averagePrice = position.calAveragePrice(
                sizeDelta,
                markPrice,
                _pnl,
                _hasProfit
            );
        }

        return position.averagePrice;
    }
}
","keccak256":"0x8d7d13ef8750f030b292938778ad0800a0f0ef62d34e41f41566650f34d04834","license":"BUSL-1.1"},"src/lib/position/PositionStorage.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;
pragma abicoder v2;

import "../utils/EnumerableValues.sol";
import "./../types/PositionStruct.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";

import {Event} from "../types/Event.sol";

struct PositionCache {
    uint16 market;
    address account;
    int256 collateralDelta;
    uint256 sizeDelta;
    uint256 markPrice;
    int256 fundingRate;
    bool isLong;
    bool isOpen;
    bytes32 sk;
    PositionProps position;
    PositionProps globalPosition;
}

library PositionStorage {
    using Position for PositionProps;
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableValues for EnumerableSet.AddressSet;
    using SafeCast for uint256;
    using SafeCast for int256;
    using SafeCast for int128;
    using SafeCast for uint128;

    bytes32 constant POS_STORAGE_POSITION = keccak256("blex.position.storage");

    event UpdatePosition(
        address indexed account,
        uint256 size,
        uint256 collateral
    );
    event RemovePosition(
        address indexed account,
        uint256 size,
        uint256 collateral
    );

    struct StorageStruct {
        // save user position, address -> position
        mapping(bytes32 => mapping(address => PositionProps)) positions;
        // set of position address
        mapping(bytes32 => EnumerableSet.AddressSet) positionKeys;
        // global position
        mapping(bytes32 => PositionProps) globalPositions;
    }

    function Storage() internal pure returns (StorageStruct storage fs) {
        bytes32 position = POS_STORAGE_POSITION;
        assembly {
            fs.slot := position
        }
    }

    function storageKey(
        uint16 market,
        bool isLong
    ) internal pure returns (bytes32 orderKey) {
        return bytes32(abi.encodePacked(isLong, market));
    }

    // =====================================================
    //           write
    // =====================================================

    function set(PositionCache memory cache) internal {
        PositionProps memory _oldPosition = Storage().positions[cache.sk][
            cache.account
        ];
        PositionProps memory _oldGlobalPosition = Storage().globalPositions[
            cache.sk
        ];
        Storage().positions[cache.sk][cache.account] = cache.position;
        Storage().globalPositions[cache.sk] = cache.globalPosition;
        Storage().positionKeys[cache.sk].add(cache.account);

        emit Event.PositionUpdate(
            cache.account,
            cache.market,
            cache.isLong,
            _oldPosition,
            cache.position
        );
        emit Event.PositionKeyAdd(cache.account, cache.market, cache.isLong);
        emit Event.GlobalPositionUpdate(
            cache.market,
            cache.isLong,
            cache.globalPosition,
            cache.globalPosition
        );
    }

    function remove(PositionCache memory cache) internal {
        PositionProps memory _oldPosition = Storage().positions[cache.sk][
            cache.account
        ];

        bool has = Storage().positionKeys[cache.sk].contains(cache.account);
        require(has, "position does not exist");
        Storage().globalPositions[cache.sk] = cache.globalPosition;
        delete Storage().positions[cache.sk][cache.account];
        Storage().positionKeys[cache.sk].remove(cache.account);

        emit Event.PositionDelete(
            cache.account,
            cache.market,
            cache.isLong,
            _oldPosition
        );
        emit Event.PositionKeyDelete(cache.account, cache.market, cache.isLong);
        emit Event.GlobalPositionUpdate(
            cache.market,
            cache.isLong,
            cache.globalPosition,
            cache.globalPosition
        );
    }
    // =====================================================
    //           view only
    // =====================================================

    function getAccountSizesForBothDirections(
        uint16 market,
        address account
    ) internal view returns (uint256 sizeLong, uint256 sizeShort) {
        sizeLong = _getPosition(market, account, true).size;
        sizeShort = _getPosition(market, account, false).size;
    }

    function getGlobalPosition(
        uint16 market,
        bool isLong
    ) internal view returns (PositionProps memory) {
        return _getGlobalPosition(storageKey(market, isLong));
    }

    function getMarketSizesForBothDirections(
        uint16 market
    ) internal view returns (uint256 globalSizeLong, uint256 globalSizeShort) {
        StorageStruct storage ps = Storage();
        globalSizeLong = ps.globalPositions[storageKey(market, true)].size;
        globalSizeShort = ps.globalPositions[storageKey(market, false)].size;
    }

    function getMarketPNLInBoth(
        uint16 market,
        uint256 longPrice,
        uint256 shortPrice
    ) internal view returns (int256) {
        int256 _totalPNL = _getMarketPNL(market, longPrice, true);
        _totalPNL += _getMarketPNL(market, shortPrice, false);
        return _totalPNL;
    }

    function getPosition(
        uint16 market,
        address account,
        uint256 markPrice,
        bool isLong
    ) internal view returns (PositionProps memory) {
        //todo
        return Storage().positions[storageKey(market, isLong)][account];
    }

    function getPositionsForBothDirections(
        uint16 market,
        address account
    )
        internal
        view
        returns (PositionProps memory posLong, PositionProps memory posShort)
    {
        StorageStruct storage ps = Storage();
        posLong = ps.positions[storageKey(market, true)][account];
        posShort = ps.positions[storageKey(market, false)][account];
    }

    function getPNL(
        uint16 market,
        address account,
        uint256 sizeDelta,
        uint256 markPrice,
        bool isLong
    ) internal view returns (int256) {
        PositionProps memory _position = getPosition(
            market,
            account,
            markPrice,
            isLong
        );
        return _calPNL(_position, sizeDelta, markPrice);
    }

    //==========================================================
    //    internal
    //==========================================================

    function _getGlobalPosition(
        bytes32 sk
    ) internal view returns (PositionProps memory _position) {
        // DONE
        _position = Storage().globalPositions[sk];
    }

    function _getMarketPNL(
        uint16 market,
        uint256 markPrice,
        bool isLong
    ) internal view returns (int256) {
        // DONE
        PositionProps memory _position = _getGlobalPosition(
            storageKey(market, isLong)
        );
        if (_position.size == 0) {
            return 0;
        }

        (bool _hasProfit, uint256 _pnl) = _position.calPNL(markPrice);
        return _hasProfit ? int256(_pnl) : -int256(_pnl);
    }

    function _getPosition(
        uint16 market,
        address account,
        bool isLong
    ) internal view returns (PositionProps memory) {
        // DONE
        return Storage().positions[storageKey(market, isLong)][account];
    }

    function _getPositionAndCalcPNL(
        uint16 market,
        address account,
        uint256 markPrice,
        bool isLong
    ) internal view returns (PositionProps memory) {
        // DONE
        PositionProps memory _position = _getPosition(market, account, isLong);

        if (markPrice == 0) {
            return _position;
        }

        if (_position.size != 0) {
            (bool _hasProfit, uint256 _realisedPnl) = _position.calPNL(
                markPrice
            );
            int256 _pnl = _hasProfit
                ? int256(_realisedPnl)
                : -int256(_realisedPnl);
            _position.realisedPnl = _pnl;
        }

        return _position;
    }

    function _calPNL(
        PositionProps memory _position,
        uint256 sizeDelta,
        uint256 markPrice
    ) internal pure returns (int256) {
        if (_position.size == 0) {
            return 0;
        }

        (bool _hasProfit, uint256 _pnl) = Position.calPNL(_position, markPrice);
        if (sizeDelta != 0) {
            _pnl = (sizeDelta * _pnl) / _position.size;
        }

        return _hasProfit ? int256(_pnl) : -int256(_pnl);
    }
}
